
def prompt(text)
  puts "#{text}"
  print "=>"
end


# Combien de handle dans array?
def give_array_length(array_name)
  prompt("Combien y a-t-il de journalistes ?")
  array_length = array_name.length.to_i
  puts array_length
  return array_length
end  


# quel handle est le plus court dans le array?


def compare_handle_length(array_name1, array_length1)

  found_shortest_handle = 100
  shortest_handle_array = []
  index = 0

  array_length1.times do
    length_pseudo = array_name1[index].length()

    if length_pseudo == found_shortest_handle
      shortest_handle_array.push array_name1[index]
    elsif length_pseudo < found_shortest_handle
      found_shortest_handle = array_name1[index].length
      shortest_handle_array = []
      shortest_handle_array.push(array_name1[index])
    else    
    end
    
    index +=1
  
  end

  return shortest_handle_array

end

def show_shortest_handle(array_name2, array_length2)
  prompt("Quels sont les handles les plus courts ?")

  shortest_handle_array = compare_handle_length(array_name2, array_length2)
  
  letters_number = shortest_handle_array[0].to_i - 1

  prompt('Les handles les plus courts sont :')
  print shortest_handle_array.to_s + "et ils mesurent #{letters_number} caractères!"
  
end


